def is_running(job) -> bool:
    return bool(set(job.job_state) & job_state_running)

def is_pending(job) -> bool:
    return bool(set(job.job_state) & job_state_pending)

def is_ended(job) -> bool:
    return bool(set(job.job_state) & job_state_ended)

def is_failed_or_completed(job) -> bool:
    return any(s in job.job_state for s in ("F", "CD"))

def is_array(job) -> bool:
    return bool(job.is_array)

